async def start(update: Update, context: ContextTypes.DEFAULT_TYPE):
    user_data = context.user_data
    user_data["current_q"] = 0
    await send_question(update, context)

async def send_question(update: Update, context: ContextTypes.DEFAULT_TYPE):
    user_data = context.user_data
    q_index = user_data["current_q"]
    
    if q_index >= len(questions):
        await update.effective_chat.send_message("ğŸ‰ Ø§Ù†ØªÙ‡Øª Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ø³Ø¦Ù„Ø©!")
        return
    
    question = questions[q_index]
    buttons = [
        [InlineKeyboardButton(text=option, callback_data=str(i))] 
        for i, option in enumerate(question["options"])
    ]
    reply_markup = InlineKeyboardMarkup(buttons)
    await update.effective_chat.send_message(
        text=f"â“ {question['q']}", reply_markup=reply_markup, parse_mode=ParseMode.HTML
    )

async def button(update: Update, context: ContextTypes.DEFAULT_TYPE):
    query = update.callback_query
    await query.answer()
    
    user_data = context.user_data
    q_index = user_data["current_q"]
    question = questions[q_index]
    selected = int(query.data)
    
    if selected == question["answer"]:
        reply_text = "âœ… Ø¥Ø¬Ø§Ø¨Ø© ØµØ­ÙŠØ­Ø©!"
    else:
        reply_text = f"âŒ Ø¥Ø¬Ø§Ø¨Ø© Ø®Ø§Ø·Ø¦Ø©! Ø§Ù„Ø¥Ø¬Ø§Ø¨Ø© Ø§Ù„ØµØ­ÙŠØ­Ø©: {question['options'][question['answer']]}"
    
    await query.edit_message_text(text=reply_text)
    user_data["current_q"] += 1
    await send_question(update, context)

